mapper.select:
select

from t_course_info
where site_id IN

    #{siteId}



    AND room_id=#{roomId}


    AND teacher_id=#{teacherId}

and (used_status=0 or used_status=1)
ORDER BY start_time ASC
limit #{SIZE}

select
from t_course_info where id = #{id,jdbcType=BIGINT} and site_id IN
#{siteId}

select

from t_course_info
where room_id = #{roomId,jdbcType=BIGINT}
and used_status=1
and site_id IN

    #{siteId}

mapper.update:
update t_course_info
set used_status=2
where site_id = #{siteId} and used_status=1 and room_id=#{roomId} and course_id!=#{courseId}
