mapper.select:
select

from t_courseware_item
where courseware_id = #{coursewareId}

    and content_type=#{contentType}

and site_id IN

    #{siteId}

ORDER BY order_num DESC ;

select

from t_courseware_item
where courseware_id IN

    #{coursewareId}


    and content_type=#{contentType}

and site_id IN

    #{siteId}

ORDER BY coursewareId ASC ,order_num DESC ;

mapper.delete:
delete from t_courseware_item
where courseware_id = #{coursewareId}
and site_id IN

    #{siteId}
